con:soapui-project.con:beforeRunScript:
import com.eviware.soapui.support.types.StringToObjectMap
def acceptedFormat = "yyyy-MM-dd" def today = new Date() def currentdate = today.format(acceptedFormat)
log.info "currentdate---"+currentdate
def year = today.getYear()+1900
log.info "year---"+year
def NextYear = year+1 log.info "NextYear---"+NextYear project.setPropertyValue("NextYear", NextYear.toString())
//Defining startDate and endDate input properties for PAC Post and Patch API's
//Defining startDate //String SystemDate=date.format("yyyy-MM-dd'T'HH:mm:ssXXX") String SystemDate=today.format("yyyy-MM-dd'T'HH:mm:ss") NewSystemDate = SystemDate + "-07:00" log.info "NewSystemDate--"+NewSystemDate project.setPropertyValue("startDate", NewSystemDate.toString())
//Defining endDate
String NextDate = today.format("MM-dd'T'HH:mm:ss") NewNextDate = NextYear + "-" + NextDate + "-07:00" log.info "NewNextDate-"+NewNextDate project.setPropertyValue("endtDate", NewNextDate.toString())

