public CreateLogFile(strRequestedFileName, existingFile=null)
	{
		//Setting up logging
		def strLogData = ""
		def fileName
		def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
		def projectPath = groovyUtils.projectPath
		def filePath
		def fileStream
		
		//Check if existing file name is requested
		if ((existingFile != null))
		{
			if (existingFile=="")
			{
				fileName = new Date().format("yyyy-MM-dd-HHmmss")
				fileName = fileName + ".txt"
			}
			else
			{
				fileName = existingFile
			}
		}
		else
		{
			fileName = new Date().format("yyyy-MM-dd-HHmmss")
			if (strRequestedFileName == null)
			{
				fileName = fileName + ".txt"
			}
			else
			{
				fileName = strRequestedFileName + "-" + fileName + ".txt"
			}
		}
		log.info "Log File Name: " + fileName
		//Check if the directory exists
		File file = new File(projectPath + "\\TestResult")
		if (!file.exists()) {file.mkdirs()}
		filePath = projectPath + "\\TestResult\\"
		
		//Create log file to write results
		fileStream = new File(filePath + fileName)
		if (!fileStream.exists())
		{
			fileStream.write(strLogData,"UTF-8")
			fileStream.createNewFile()
		}
		return fileStream
	}

