import groovy.time.TimeCategory

def acceptedFormat = "yyyy-MM-dd"
def today = new Date()
def currentdate = today.format(acceptedFormat)

use(TimeCategory) {
    def oneYear = today + 1.year
    def NextYear =  oneYear.format("yyyy")
    testRunner.testCase.testSuite.setPropertyValue("NextYear", NextYear)
    
}

//Defining startDate and endDate input properties for PAC Post and Patch API's
Date date= new Date()
//Defining startDate //String SystemDate=date.format("yyyy-MM-dd'T'HH:mm:ssXXX") String SystemDate=date.format("yyyy-MM-dd'T'HH:mm:ss") NewSystemDate = SystemDate + "-07:00" log.info NewSystemDate
//Defining endDate String NextYear = testRunner.testCase.testSuite.getPropertyValue("NextYear") String NextDate = date.format("MM-dd'T'HH:mm:ss") NewNextDate = NextYear + "-" + NextDate + "-07:00" log.info NewNextDate
testRunner.testCase.testSuite.setPropertyValue("startDate", NewSystemDate) testRunner.testCase.testSuite.setPropertyValue("endtDate", NewNextDate)

