import groovy.time.TimeCategory

def acceptedFormat = "yyyy-MM-dd"
def today = new Date()
def currentdate = today.format(acceptedFormat)

use(TimeCategory) {
    def oneYear = today + 1.year
    def NextYear =  oneYear.format("yyyy")
    testRunner.testCase.testSuite.project.setPropertyValue("NextYear", NextYear)
    
}

